import React from 'react';
import { connect } from 'react-redux';
import { Link, RouteComponentProps } from 'react-router-dom';
import { Container, Button, Progress, Table } from 'reactstrap';
// tslint:disable-next-line:no-unused-variable
import { Translate, ICrudGetAction, TextFormat } from 'react-jhipster';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';

import { IRootState } from 'app/shared/reducers';
import { getEntity, getEntityStatus } from './import-fichier.reducer';
import { IImportFichier } from 'app/shared/model/import-fichier.model';
// tslint:disable-next-line:no-unused-variable
import { APP_DATE_FORMAT, APP_LOCAL_DATE_FORMAT } from 'app/config/constants';


export interface IImportFichierDetailProps extends StateProps, DispatchProps, RouteComponentProps<{ id: string }> {}

export class ImportFichierDetail extends React.Component<IImportFichierDetailProps> {

  componentDidMount() {
    this.props.getEntity(this.props.match.params.id);
  }

  render() {
    const { importFichierEntity, status } = this.props;
    return (
      <Container body className="text-center">
        <h2>
          <Translate contentKey="cleanclientApp.importFichier.detail.title">ImportFichier</Translate> [<b>{importFichierEntity.id}</b>]
        </h2>     
        <dl className="jh-entity-details">   
          <dt>
            <span id="dateDeDebut">
              <Translate contentKey="cleanclientApp.importFichier.dateDeDebut">Date De Debut</Translate>
            </span>
          </dt>
            <dd>
              <TextFormat value={importFichierEntity.dateDeDebut} type="date" format={APP_LOCAL_DATE_FORMAT} />
            </dd>        
          <dt>
            <Translate contentKey="cleanclientApp.importFichier.user">User</Translate>
          </dt>
            <dd>{importFichierEntity.user ? importFichierEntity.user.id : ''}</dd>    
        </dl>  
        <Table body className="text-left">
          <thead>
            <th>Nettoyage de vos données<p></p><Progress value="90" max={100}> 90% </Progress>
            </th>
          </thead>
          <tbody>
            <tr>
              <td>Clients existants trouvés<p></p><Progress value="60" max={100}> 60% </Progress>
              </td>
            </tr>
            <tr>
              <td>Clients existants à confirmer<p></p><Progress value="20" max={100}> 20% </Progress>
              </td>
            </tr>
            <tr>
              <td>KO<p></p><Progress value="10" max={100}> 10 </Progress>
              </td>
            </tr>
          </tbody>
        </Table>
        <Table body className="text-left">
          <thead>
            <th>Prospects identifiés<p></p><Progress value="55" max={100}> 55% </Progress>
            </th>
          </thead> 
        </Table>
          <Button tag={Link} to="/entity/import-fichier" replace color="info">
              <FontAwesomeIcon icon="arrow-left" />{' '}
              <span className="d-none d-md-inline">
                <Translate contentKey="entity.action.back">Back</Translate>
              </span>
            </Button>
            &nbsp;
          <Button tag={Link} to={`/entity/import-fichier/${importFichierEntity.id}/edit`} replace color="primary">
            <FontAwesomeIcon icon="pencil-alt" />{' '}
            <span className="d-none d-md-inline">
              <Translate contentKey="entity.action.edit">Edit</Translate>
            </span> 
          </Button>
            &nbsp;
          <Button replace color="success">
            <FontAwesomeIcon icon="file-export" />{' '}
            <span className="d-none d-md-inline">
              Exporter
            </span>
          </Button>
      </Container>

    );
  }
}

const mapStateToProps = ({ importFichier }: IRootState) => ({
  importFichierEntity: importFichier.entity,
  status: importFichier.entity

});

const mapDispatchToProps = { getEntity, getEntityStatus };

type StateProps = ReturnType<typeof mapStateToProps>;
type DispatchProps = typeof mapDispatchToProps;


export default connect(
  mapStateToProps,
  mapDispatchToProps
)(ImportFichierDetail);
